INSERT INTO `evaluate` (`id`, `evaluate_level`, `evaluate_waktu`, `evaluate_points`, `evaluate_reviews`, `evaluate_detail`, `user`) VALUES
(6, 'Ya', 'Ya', 'Ya', 'Ya', 'Saya berusaha belajar semampunya diri saya karena belajar ada proses untuk Menjadi seorang yang sukses dan orang yang sukses sangat lah tidak semudah apa yang kita bayangkan perlu tenaga pikiran dan waktu luang .', 15),
(7, 'Ya', 'Ya', 'Ya', 'Ya', 'tidak ada kata berhasil bagi si pemalas', 23),
(8, 'Ya', 'Tidak', 'Ya', 'Ya', 'Waktu pembelajar sangat cepat dan saya sabtu masih kerja', 30),
(9, 'Ya', 'Ya', 'Ya', 'Tidak', 'Akan lebih baik jika ada video penjelasannya juga,, tapi panduan coding juga sudah sangat membantu dengan jelas. Terima kasih MR. Hanafri.', 34),
(10, 'Ya', 'Ya', 'Ya', 'Ya', 'Seru belajarnya, mudah dipahami:)) kendalanya ndaa ada, karena banyak lebihnya singkat, padat dan jelas maksudnya tujuan pembelajarannya, jadi semangat buat belajar ðŸŒˆâœ¨â˜€ï¸ Terimakasih pak:))', 33),
(11, 'Ya', 'Ya', 'Ya', 'Ya', 'Asik ,seru, dan santuy aja sih :)\r\nBelajar bisa di mana saja dan kapan saja .\r\nKendala nya kalo ngoding di hp gak selengkap kalo ngoding di laptope atau di pc sih.\r\n', 9),
(12, 'Ya', 'Tidak', 'Ya', 'Ya', 'metode pembelajaran ini sangat membantu dan sangat mudah dipahami, yang menjadi kendala disaat memperhatikan antara titik dan koma.', 28),
(13, 'Ya', 'Ya', 'Ya', 'Ya', 'Hanya terkadang jarang mengaplikasikan pembelajaran jadi beberapa terasa asing, motivasi saya adalah ingin mencari dan mencapai sebuah keberhasilan dan hal positif, dan kendalanya adalah hanya pada kondisi saat ini, kuota internet gabisa beli lagi , pemasukan sedikit.', 35),
(14, 'Tidak', 'Tidak', 'Tidak', 'Tidak', 'Dengan metode seperti ini sangat profesional banget, kendalanya kurang pemahaman d materi', 32),
(15, 'Ya', 'Ya', 'Ya', 'Ya', 'Yang menjadi motivasi saya balajar css dan html yaa jelas karena ingin bisa bikin web sendiri tanpa harus googling, paling googling cuma buat nyari warna sama style.\r\n\r\nDan kendalanya hanya bahasanya saja yang belum terlalu paham dan hafal. Untuk logika mungkin ngerti tapi kalo ngga bisa bahasanya yaa percuma.', 46),
(16, 'Ya', 'Ya', 'Ya', 'Ya', 'Yang menajdi kenda adalah..jaringan telpon yang lama', 52),
(17, 'Ya', 'Ya', 'Tidak', 'Ya', 'Evaluasinya adalah kurangnya pemahaman saya tentang materi ini &amp; bisa menambah ilmu sedikit demi sedikit. motivasi saya hanyalah orang tua. kendalanya adalah kurangnya pendamping untuk memahami materi', 47),
(18, 'Ya', 'Ya', 'Ya', 'Ya', 'Ingin memahami pembuatan web dengan maksimal.\r\nKendalanya adalah masih perlu banyak praktek agar dapat mengingat setiap elemen syntax', 60),
(19, 'Ya', 'Ya', 'Ya', 'Ya', 'css adalah singkatan dari cascading style sheets yang berguna untuk merancang ataupun mendesain web sesuai kebutuhan. \r\n\r\nMotivasi:ingin mengetahui\r\n\r\nKendala:ruang waktu yang terbatas ', 48),
(20, 'Ya', 'Ya', 'Ya', 'Ya', 'Yang menjadi motivasi yaitu metode pembelajaran nya yang terdapat penjelasan singkat dan soal soal latihan yang bisa dikerjakan langsung\r\nYang menjadi kendalanya di rasa malas nya untuk membaca apalagi penjelasannya yang panjang', 55),
(21, 'Ya', 'Ya', 'Ya', 'Ya', 'Terkadang rasa menyerah itu ada apabila ketika kita melihat soal yang begitu rumit dan belum kita ketahui sama sekali materi tersebut tetapi dengan segenap kemauaan kamu dapat memecahkan segala soal yang rumit itu. Semangatlah  jangan menyerah sampai di sini saja!', 58),
(22, 'Ya', 'Tidak', 'Ya', 'Ya', 'motivasi saya yaitu bisa mendapatkan ilmu baru dan bisa membagi ilmu nya kepada orang-oang yang ingin belajar', 45),
(23, 'Ya', 'Tidak', 'Ya', 'Ya', 'Motivasi belajar : Agar lulus di waktu yg tepat\r\nkendala : Masih susah untuk membagi waktu', 44),
(24, 'Ya', 'Ya', 'Ya', 'Ya', 'materi yang saya pelajari mudah sekali dipahami dan dimengerti, dan selama 3 hari saya mendapatkan banyak sekali ilmu dari materi atau pelajaran yang saya dapat dari pembelajaran ini.\r\n\r\nmotivasi saya ingin mengenal aplikasi CSS dengan baik dan kendala yang saya dapat adalah banyak codingan yang kurang saya paham dan sulit untuk memahaminya dengan baik.', 49),
(25, 'Ya', 'Ya', 'Ya', 'Tidak', 'Terkendala pada koneksi internet yang tidak stabil dan web ada yang kurang responsive. ', 53);
