INSERT INTO `reflect` (`id`, `user`, `reflection`) VALUES
(13, 8, '&lt;html&gt;\r\n&lt;head&gt; \r\n	&lt;title&gt;Refleksi level 1 &lt;/title&gt; \r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n	&lt;h1&gt;Saya sudah belajar CSS level 1 &lt;/h1&gt;\r\n	&lt;p&gt;Text ini berada dalam satu paragraf.&lt;/p&gt;\r\n&lt;/body&gt; \r\n&lt;/html&gt;\r\n'),
(14, 9, '\r\n\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n  &lt;title&gt;Contoh Internal CSS&lt;/title&gt;\r\n  &lt;!-- penulisan internal css dalam tag head --&gt;\r\n  &lt;style type=&quot;text/css&quot;&gt;\r\n    p{\r\n      font-family: serif;\r\n      line-height: 1.75em;\r\n      font-size: 18px;\r\n    }\r\n    i { \r\n      font-family: sans;\r\n      color: orange;\r\n    }\r\n  &lt;/style&gt;\r\n&lt;/head&gt;\r\n\r\n&lt;body&gt;\r\n  &lt;!-- penulisan internal css dalam tag body --&gt;\r\n  &lt;style type=&quot;text/css&quot;&gt;\r\n    h2 { \r\n      font-family: sans;\r\n      color: #333;\r\n    }\r\n  &lt;/style&gt;\r\n  &lt;h2&gt;Ini Contoh modifikasi css&lt;/h2&gt;\r\n  &lt;p&gt; &lt;i&gt; css mempermudah dan mengedit tampilan web &lt;/i&gt; &lt;/p&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;'),
(15, 8, 'p {text-align : center; color : white;}'),
(16, 8, 'p, h1, h2 {text-align: left; color : gold;}'),
(17, 8, '&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\nbody {\r\n  background-color: grey;\r\n}\r\n\r\nh1 {\r\n  color: gold;\r\n  margin-left: 40px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1&gt;Ini adalah heading&lt;/h1&gt;\r\n&lt;p&gt;Ini adalah paragraf.&lt;/p&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;'),
(18, 8, '&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\nbody {\r\n  background-color: linen;\r\n}\r\n\r\nh1 {\r\n  color: maroon;\r\n  margin-left: 40px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1&gt;Ini adalah heading&lt;/h1&gt;\r\n&lt;p&gt;Ini adalah paragraf.&lt;/p&gt; /*ini untuk menambahkan komentar CSS*/ \r\n&lt;!-- ini untuk komentar pada html--&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;'),
(19, 8, '\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1 style=&quot;background-color:Tomato;&quot;&gt;Tomato&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:Orange;&quot;&gt;Orange&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:DodgerBlue;&quot;&gt;DodgerBlue&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:MediumSeaGreen;&quot;&gt;MediumSeaGreen&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:Gray;&quot;&gt;Gray&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:SlateBlue;&quot;&gt;SlateBlue&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:Violet;&quot;&gt;Violet&lt;/h1&gt;\r\n&lt;h1 style=&quot;background-color:LightGray;&quot;&gt;LightGray&lt;/h1&gt;\r\n\r\n&lt;!-- Untuk warna-warna standar lainnya gunakan nama warna dalam bahasa Inggris, misal : yellow, red, blue, dsb. --&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n\r\n\r\n'),
(20, 8, '\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\nbody {\r\n  background-image: url(&quot;images/paper.jpg&quot;);\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1&gt;Hello World!&lt;/h1&gt;\r\n\r\n&lt;p&gt;Halaman ini menggunakan gambar sebagai background!&lt;/p&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n\r\n\r\n\r\n\r\n'),
(21, 8, '\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\np.one {\r\n  border-style: solid;\r\n  border-width: 5px;\r\n}\r\n\r\np.two {\r\n  border-style: solid;\r\n  border-width: medium;\r\n}\r\n\r\np.three {\r\n  border-style: dotted;\r\n  border-width: 2px;\r\n}\r\n\r\np.four {\r\n  border-style: dotted;\r\n  border-width: thick;\r\n}\r\n\r\np.five {\r\n  border-style: double;\r\n  border-width: 15px;\r\n}\r\n\r\np.six {\r\n  border-style: double;\r\n  border-width: thick;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h2&gt;Properti border-width&lt;/h2&gt;\r\n&lt;p&gt;Properti width / lebar dari 4 sisi border:&lt;/p&gt;\r\n\r\n&lt;p class=&quot;one&quot;&gt;Ini adalah teks.&lt;/p&gt;\r\n&lt;p class=&quot;two&quot;&gt;Ini adalah teks.&lt;/p&gt;\r\n&lt;p class=&quot;three&quot;&gt;Ini adalah teks.&lt;/p&gt;\r\n&lt;p class=&quot;four&quot;&gt;Ini adalah teks.&lt;/p&gt;\r\n&lt;p class=&quot;five&quot;&gt;Ini adalah teks.&lt;/p&gt;\r\n&lt;p class=&quot;six&quot;&gt;Ini adalah teks.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;b&gt;Note:&lt;/b&gt; Properti &quot;border-width&quot; tidak akan bekerja apabila digunakan secara mandiri / tanpa menggunakan properti border utama. \r\nPastikan spesifikkan properti &quot;border-style&quot; yang digunakan terlebih dahulu.&lt;/p&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n\r\n\r\n\r\n'),
(22, 8, '\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\ndiv {\r\n  border: 1px solid red;\r\n  margin-left: 100px;\r\n}\r\n\r\np.ex1 {\r\n  margin-left: inherit;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h2&gt;Penggunaan nilai inherit&lt;/h2&gt;\r\n&lt;p&gt;Margin kiri diwarisi dari elemen induk/yang lebih luas:&lt;/p&gt;\r\n\r\n&lt;div&gt;\r\n&lt;p class=&quot;ex1&quot;&gt;Paragraf ini memiliki margin kiri yang diwarisi (dari elemen div).&lt;/p&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n\r\n\r\n'),
(23, 15, '&lt;html&gt;\r\n&lt;head&gt; \r\n	&lt;title&gt;Ruang koding Pemrograman&lt;/title&gt; \r\n&lt;style&gt;\r\n* {\r\nbackground-color: black;\r\nmargin-top: 5px;\r\nmargin-left: 7px;\r\ncolor: white;\r\n}\r\ntable, th, td{\r\nborder: solid white 1px;\r\n}\r\n.D {\r\nfont-size: 19px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n	&lt;h1&gt;Tabel Mahasiswa&lt;/h1&gt;\r\n        &lt;p class=&quot;D&quot;&gt;Daftar Mahasiswa Stmik Glboal&lt;/p&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;\r\n&lt;table&gt;\r\n&lt;tr&gt;\r\n&lt;th&gt;NO&lt;/th&gt;\r\n&lt;th&gt;NAMA&lt;/th&gt;\r\n&lt;th&gt;KELAS&lt;/th&gt;\r\n&lt;th&gt;JURUSAN&lt;/th&gt;\r\n&lt;th&gt;ALAMAT&lt;/th&gt;\r\n&lt;/tr&gt;\r\n&lt;tr&gt;\r\n&lt;td&gt;1&lt;/td&gt;\r\n&lt;td&gt;Muhammad Fadilah&lt;/td&gt;\r\n&lt;td&gt;Malam&lt;/td&gt;\r\n&lt;td&gt;Sofware Engineering&lt;/td&gt;\r\n&lt;td&gt;Sangiang&lt;/td&gt;\r\n&lt;/tr&gt;\r\n&lt;tr&gt;\r\n&lt;td&gt;2&lt;/td&gt;\r\n&lt;td&gt;Dodi&lt;/td&gt;\r\n&lt;td&gt;Shift&lt;/td&gt;\r\n&lt;td&gt;Accounting&lt;/td&gt;\r\n&lt;td&gt;Perum&lt;/td&gt;\r\n&lt;/tr&gt;\r\n&lt;td&gt;3&lt;/td&gt;\r\n&lt;td&gt;jaka&lt;/td&gt;\r\n&lt;td&gt;Malam&lt;/td&gt;\r\n&lt;td&gt;Computer Networking&lt;/td&gt;\r\n&lt;td&gt;Jati Uwung&lt;/td&gt;\r\n&lt;/tr&gt;\r\n&lt;td&gt;4&lt;/td&gt;\r\n&lt;td&gt;Haris&lt;/td&gt;\r\n&lt;td&gt;Malam&lt;/td&gt;\r\n&lt;td&gt;Computer Scient &lt;/td&gt;\r\n&lt;td&gt;Kota Bumi&lt;/td&gt;\r\n&lt;/tr&gt;\r\n&lt;td&gt;5&lt;/td&gt;\r\n&lt;td&gt;Edo&lt;/td&gt;\r\n&lt;td&gt;Shift&lt;/td&gt;\r\n&lt;td&gt;Computer Design&lt;/td&gt;\r\n&lt;td&gt;Regency&lt;/td&gt;\r\n&lt;/tr&gt;\r\n&lt;/table&gt;\r\n&lt;/body&gt; \r\n&lt;/html&gt;'),
(24, 16, '&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\nbody {\r\n  background-color: lightblue;\r\n}\r\n\r\nh1 {\r\n  color: white;\r\n  text-align: center;\r\n}\r\n\r\np {\r\n  font-family: verdana;\r\n  font-size: 20px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1&gt;My First CSS Example&lt;/h1&gt;\r\n&lt;p&gt;This is a paragraph.&lt;/p&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n'),
(25, 16, '&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\nbody {\r\nbackground-color:black;\r\n}\r\n\r\nh1 {\r\ncolor:blue;\r\ntext-align:center;\r\n}\r\n\r\np {\r\n  color: red;\r\n  text-align: center;\r\n} \r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1&gt;Selamat Datang!&lt;/h1&gt;\r\n&lt;p&gt;Hello World!&lt;/p&gt;\r\n&lt;p&gt;Paragraf ini diberikan gaya dengan CSS.&lt;/p&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n'),
(26, 17, '&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;style&gt;\r\nbody {\r\n  background-color: lightblue;\r\n}\r\n\r\nh1 {\r\n  color: white;\r\n  text-align: center;\r\n}\r\n\r\np {\r\n  font-family: verdana;\r\n  font-size: 20px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;h1&gt;My First CSS Example&lt;/h1&gt;\r\n&lt;p&gt;This is a paragraph.&lt;/p&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n'),
